INSERT INTO `app_powerbook` (`id`, `title`, `content`, `mainImg`, `main`, `guideMain`) VALUES
(84, 'Warrior Skills', '<div class=\"wiki__group\" id=\"10216-0\" style=\"font-family: &quot;Noto Sans&quot;, &quot;Noto Sans KR&quot;, -apple-system, BlinkMacSystemFont, Roboto, Arial, sans-serif; padding-top: 0px; color: rgb(0, 0, 0); font-size: medium;\">\r\n   <div class=\"wiki__body\" style=\"letter-spacing: 0px !important;\">\r\n      <p style=\"letter-spacing: 0px !important;\">An exclusive skills used only by Warriors.</p>\r\n      <p style=\"letter-spacing: 0px !important;\"></p>\r\n      <p style=\"letter-spacing: 0px !important;\">It has a variety of skills, ranging from general skills to deal with opponents, to passive skills that temporarily increase one\'s AC and HP.</p>\r\n      <p style=\"letter-spacing: 0px !important;\">There is also a passive technique that automatically counterattacks the opponent\'s close range, long range, and magic attacks with a certain probability.</p>\r\n      <p style=\"letter-spacing: 0px !important;\"></p>\r\n      <p style=\"letter-spacing: 0px !important;\">Can learn normal level 1 magic at level 50</p>\r\n   </div>\r\n</div>\r\n<div class=\"wiki__group\" id=\"10216-1\" style=\"font-family: &quot;Noto Sans&quot;, &quot;Noto Sans KR&quot;, -apple-system, BlinkMacSystemFont, Roboto, Arial, sans-serif; color: rgb(0, 0, 0); font-size: medium;\">\r\n   <div class=\"wiki__h2\" style=\"letter-spacing: 0px !important;\">\r\n      <h2 style=\"font-family: &quot;Noto Sans&quot;, &quot;Noto Sans KR&quot;, -apple-system, BlinkMacSystemFont, Roboto, Arial, sans-serif; letter-spacing: 0px !important;\">Features</h2>\r\n   </div>\r\n   <div class=\"wiki__body\" style=\"letter-spacing: 0px !important;\">\r\n      <p style=\"letter-spacing: 0px !important;\">&#8594; It has a passive skill that automatically counterattacks the opponent\'s close range, long range, and magic attacks with a certain probability.</p>\r\n      <p style=\"letter-spacing: 0px !important;\">&#8594; When using a skill, a certain amount of crystals are consumed.</