INSERT INTO `app_powerbook` (`id`, `title`, `content`, `mainImg`, `main`, `guideMain`) VALUES
(85, 'Normal Magic', '<p style=\"font-family: &quot;Noto Sans&quot;, &quot;Noto Sans KR&quot;, -apple-system, BlinkMacSystemFont, Roboto, Arial, sans-serif; color: rgb(73, 73, 73);\">Normal magic used by several classes including the Wizards.</p>\r\n<p style=\"font-family: &quot;Noto Sans&quot;, &quot;Noto Sans KR&quot;, -apple-system, BlinkMacSystemFont, Roboto, Arial, sans-serif; color: rgb(73, 73, 73);\">Variety of buffs, recovery, debuffs, and attack.  The Wizard is the only class that can learn up to level 10 of normal magic.</p>\r\n<div class=\"wiki__group\" id=\"39-1\" style=\"font-family: &quot;Noto Sans&quot;, &quot;Noto Sans KR&quot;, -apple-system, BlinkMacSystemFont, Roboto, Arial, sans-serif; color: rgb(0, 0, 0); font-size: medium;\">\r\n   <div class=\"wiki__h2\" style=\"letter-spacing: 0px !important;\">\r\n      <h2 style=\"font-family: &quot;Noto Sans&quot;, &quot;Noto Sans KR&quot;, -apple-system, BlinkMacSystemFont, Roboto, Arial, sans-serif; letter-spacing: 0px !important;\">How to Learn</h2>\r\n   </div>\r\n   <div class=\"wiki__body\" style=\"letter-spacing: 0px !important;\">\r\n      <font color=\"#000000\" style=\"letter-spacing: 0px !important;\">\r\n         <p style=\"letter-spacing: 0px !important;\"><font color=\"#000000\" style=\"letter-spacing: 0px !important;\"></font></p>\r\n         <div class=\"images simg\" style=\"clear: both; letter-spacing: 0px !important;\">\r\n            <div class=\"img_right\" style=\"margin-bottom: 30px; margin-left: 50px; float: right; text-align: right; letter-spacing: 0px !important;\">\r\n               <font color=\"#000000\" style=\"letter-spacing: 0px !important;\">\r\n                  <img src=\"https://assets.playnccdn.com/gamedata/powerbook/lineage/66/67/8194c416809f3284e3af695f.jpg\" class=\"upimg\" style=\"border-style: none; max-width: 100%; max-height: 100%; letter-spacing: 0px !important;\">\r\n                  <p class=\"caption\" style=\"padding-top: 6px; max-width: 340px; text-align: 