INSERT INTO `app_powerbook` (`id`, `title`, `content`, `mainImg`, `main`, `guideMain`) VALUES
(91, 'Illusionist Magic', '<div class=\"wiki__group\" id=\"4816-0\" style=\"font-family: &quot;Noto Sans&quot;, &quot;Noto Sans KR&quot;, -apple-system, BlinkMacSystemFont, Roboto, Arial, sans-serif; padding-top: 0px; color: rgb(0, 0, 0); font-size: medium;\">\r\n   <div class=\"wiki__body\" style=\"letter-spacing: 0px !important;\">\r\n      <p style=\"letter-spacing: 0px !important;\">The Illusionist has their very own special magic.</p>\r\n      <p style=\"letter-spacing: 0px !important;\"><br style=\"letter-spacing: 0px !important;\"></p>\r\n      <p style=\"letter-spacing: 0px !important;\">Illusionist carries varies buff type spells for self, parties, and allies. </p>\r\n      <p style=\"letter-spacing: 0px !important;\"></p>\r\n      <p style=\"letter-spacing: 0px !important;\">Illusionist magic can be learned by purchasing or hunting memory crystals.</p>\r\n   </div>\r\n</div>\r\n<div class=\"wiki__group\" id=\"4816-1\" style=\"font-family: &quot;Noto Sans&quot;, &quot;Noto Sans KR&quot;, -apple-system, BlinkMacSystemFont, Roboto, Arial, sans-serif; color: rgb(0, 0, 0); font-size: medium;\">\r\n   <div class=\"wiki__h2\" style=\"letter-spacing: 0px !important;\">\r\n      <h2 style=\"font-family: &quot;Noto Sans&quot;, &quot;Noto Sans KR&quot;, -apple-system, BlinkMacSystemFont, Roboto, Arial, sans-serif; letter-spacing: 0px !important;\">Features</h2>\r\n   </div>\r\n   <div class=\"wiki__body\" style=\"letter-spacing: 0px !important;\">\r\n      <p style=\"letter-spacing: 0px !important;\">&#8594; Some spells deal low damage but ignore the opponent\'s MR and Elemental Resistances.<br>&#8594; Uses cube magic that simultaneously gives postivie buffs to allies and negative buffs to enemies.<br>&#8594; Elemental stones, which are consumables needed to use illusionist magic, are sold by general stores in each village.</p>\r\n   </div>\r\n</div>\r\n<div class=\"wiki__group\" id=\"4816-2\" style=\"font-family: &quot;Noto Sans&quot;, &quot;Noto Sans KR&quot;, -apple-system, BlinkMacSystemFont, Roboto, Arial, sans-serif; color: rgb(0, 0, 0); font-size: medium;\">\r\n   <div class=\"wiki__h2\" style=\"letter-spacing: 0px !important;\">\r\n      <h2 style=\"font-family: &quot;Noto Sans&quot;, &quot;Noto Sans KR&quot;, -apple-system, BlinkMacSystemFont, Roboto, Arial, sans-serif; letter-spacing: 0px !important;\">How to Learn</h2>\r\n   </div>\r\n   <div class=\"wiki__body\" style=\"letter-spacing: 0px !important;\">\r\n      <font color=\"#000000\" style=\"letter-spacing: 0px !important;\">\r\n         <p style=\"letter-spacing: 0px !important;\"></p>\r\n         <p style=\"letter-spacing: 0px !important;\"></p>\r\n      </font>\r\n      <p style=\"letter-spacing: 0px !important;\"><span style=\"color: rgb(0, 0, 0); letter-spacing: 0px !important;\">Illusionist can learn their exclusive magic from Level 15.</span><span style=\"color: rgb(0, 0, 0); letter-spacing: 0px !important;\">The higher you level, the more magic you can learn.</span></p>\r\n      <p style=\"letter-spacing: 0px !important;\"><span style=\"color: rgb(0, 0, 0); letter-spacing: 0px !important;\"><br style=\"letter-spacing: 0px !important;\"></span></p>\r\n      <p style=\"letter-spacing: 0px !important;\"><font color=\"#000000\" style=\"letter-spacing: 0px !important;\">Some Memory Crystals can be purchased from Talking Island Village by NPC <font color=\"#000000\" style=\"letter-spacing: 0px !important;\">Simon.</font><font color=\"#000000\" style=\"letter-spacing: 0px !important;\"></font></font><font color=\"#000000\" style=\"letter-spacing: 0px !important;\">Other spells can be purchased by other players or by hunting.</font></p>\r\n      <p style=\"letter-spacing: 0px !important;\"></p>\r\n      <p style=\"letter-spacing: 0px !important;\"></p>\r\n      <table class=\"custom-table\" style=\"clear: both; width: 1260px; table-layout: fixed; letter-spacing: 0px !important;\">\r\n         <tbody style=\"letter-spacing: 0px !important;\">\r\n            <tr st